<?phpfunction getAliasTags( $input = null ) {	global $db;	$sql = 'SELECT a.alias, t.tag FROM l_aliases a, l_tags t WHERE a.tag_id = t.id';	if ( $input ) {		$sql .= $db->replaceholders(' AND a.alias IN (?)', array($input));	}	$sql .= ' ORDER BY a.alias';	return $db->fetch_fields($sql);}function html( $html ) {	return htmlspecialchars($html, ENT_COMPAT, 'UTF-8');}function valid_tags( $f_szTags ) {	return implode(' ', array_filter(array_map('valid_tag', explode(' ', $f_szTags))));}function valid_tag( $f_szTag ) {	return strtolower(preg_replace("%[^~a-zA-Z0-9\.\-\+_//]%", '', $f_szTag));}